const items = $input.all();

let dashboard = {};
let crawlTasks = {};
let outbox = {};
let crawledRaw = {};
let externalHealth = {};
let productSyncFailures = {};
let crawlExecutions = {};

for (const item of items) {
  const raw = item.json;
  const data = raw.data || raw;
  if (data.activeSchedulers !== undefined) dashboard = data;
  else if (data.stuckTasks !== undefined) crawlTasks = data;
  else if (data.crawlTaskOutbox !== undefined) outbox = data;
  else if (data.totalRaw !== undefined) crawledRaw = data;
  else if (data.systems !== undefined) externalHealth = data;
  else if (data.failureCountsBySyncType !== undefined) productSyncFailures = data;
  else if (data.successRate !== undefined) crawlExecutions = data;
}

// ─── Thresholds ───
const PENDING_TH = 100;
const PROCESSING_TH = 50;
const FAILED_TH = 0;
const STUCK_TH = 5;
const EXT_FAIL_TH = 3;
const ERROR_TH = 10;
const EXEC_SUCCESS_RATE_TH = 90;

const cnt = (d, s) => (d && d.countsByStatus && d.countsByStatus[s]) || 0;

const extractOb = (d) => ({
  pending: cnt(d, 'PENDING'), processing: cnt(d, 'PROCESSING'),
  completed: cnt(d, 'COMPLETED'), failed: cnt(d, 'FAILED'),
  total: (d && d.total) || 0
});

// ─── Detect Alert Reasons ───
const reasons = [];

if (dashboard.overallStatus && dashboard.overallStatus !== 'HEALTHY')
  reasons.push(':warning: 시스템 상태 `' + dashboard.overallStatus + '`');
if ((dashboard.recentErrors || 0) > ERROR_TH)
  reasons.push(':x: 최근 에러 `' + dashboard.recentErrors + '`건');
if ((crawlTasks.stuckTasks || 0) > STUCK_TH)
  reasons.push(':snail: stuck 태스크 `' + crawlTasks.stuckTasks + '`건');

var ctF = cnt(crawlTasks, 'FAILED');
if (ctF > FAILED_TH) reasons.push(':x: CrawlTask FAILED `' + ctF + '`건');

var obTypes = [
  { n: 'CrawlTask', d: outbox.crawlTaskOutbox },
  { n: 'Scheduler', d: outbox.schedulerOutbox },
  { n: 'ProductSync', d: outbox.productSyncOutbox }
];
for (var i = 0; i < obTypes.length; i++) {
  var ob = obTypes[i];
  var p = cnt(ob.d, 'PENDING'), pr = cnt(ob.d, 'PROCESSING'), f = cnt(ob.d, 'FAILED');
  if (p > PENDING_TH) reasons.push(':hourglass: ' + ob.n + ' Outbox PENDING `' + p + '`건');
  if (pr > PROCESSING_TH) reasons.push(':warning: ' + ob.n + ' Outbox stuck의심 `' + pr + '`건');
  if (f > FAILED_TH) reasons.push(':x: ' + ob.n + ' Outbox FAILED `' + f + '`건');
}

var rawF = cnt(crawledRaw, 'FAILED');
if (rawF > FAILED_TH) reasons.push(':x: CrawledRaw FAILED `' + rawF + '`건');

var systems = externalHealth.systems || [];
for (var j = 0; j < systems.length; j++) {
  var s = systems[j];
  if (s.status !== 'HEALTHY' || s.recentFailures > EXT_FAIL_TH)
    reasons.push(':satellite: ' + s.system + ' `' + s.status + '` 실패 ' + s.recentFailures + '건');
}

// ─── ProductSync Failures ───
var psTotal = productSyncFailures.totalFailures || 0;
if (psTotal > 0) {
  var syncTypes = productSyncFailures.failureCountsBySyncType || {};
  var syncDetail = Object.entries(syncTypes).map(function(e) { return e[0] + ': ' + e[1]; }).join(', ');
  reasons.push(':package: ProductSync 실패 `' + psTotal + '`건 (' + syncDetail + ')');
}

// ─── CrawlExecution ───
var execRate = crawlExecutions.successRate || 0;
var execTotal = crawlExecutions.totalExecutions || 0;
if (execTotal > 0 && execRate < EXEC_SUCCESS_RATE_TH)
  reasons.push(':runner: CrawlExecution 성공률 `' + execRate.toFixed(1) + '%` (기준: ' + EXEC_SUCCESS_RATE_TH + '%)');
var execTimeout = cnt(crawlExecutions, 'TIMEOUT');
if (execTimeout > 0)
  reasons.push(':clock3: CrawlExecution TIMEOUT `' + execTimeout + '`건');

var needsAlert = reasons.length > 0;

// ─── KST Time ───
var kst = new Date(Date.now() + 9 * 3600000);
var ts = kst.toISOString().replace('T', ' ').slice(0, 19) + ' KST';

var tOb = extractOb(outbox.crawlTaskOutbox);
var sOb = extractOb(outbox.schedulerOutbox);
var pOb = extractOb(outbox.productSyncOutbox);
var taskTotal = crawlTasks.totalTasks || 0;
var taskStuck = crawlTasks.stuckTasks || 0;
var rawTotal = crawledRaw.totalRaw || 0;

var st = dashboard.overallStatus || 'UNKNOWN';
var stEmoji = (st === 'HEALTHY') ? ':large_green_circle:' : ':red_circle:';

// ─── Outbox row formatter ───
var fmtObRow = function(label, d) {
  if (d.total === 0) return '';
  var cols = [];
  cols.push(d.pending > 0 ? '`' + d.pending + '`' : '`-`');
  cols.push(d.processing > 0 ? '`' + d.processing + '`' : '`-`');
  cols.push(d.completed > 0 ? '`' + d.completed + '`' : '`-`');
  cols.push(d.failed > 0 ? '*`' + d.failed + '`*' : '`-`');
  return '>  ' + label + '  ' + cols.join('    ');
};

// ─── Status badge formatter ───
var statusBadge = function(status) {
  if (status === 'HEALTHY') return ':large_green_circle: `HEALTHY`';
  if (status === 'DEGRADED') return ':large_yellow_circle: `DEGRADED`';
  return ':red_circle: `' + status + '`';
};

// ─── Alert Message ───
var a = [];
a.push(':rotating_light:  *CrawlingHub 모니터링 알람*');
a.push(':clock1:  ' + ts);
a.push('');
a.push('━━━━━━━━━━━━━━━━━━━━━━━━━━━━');
a.push(':mega:  *알람 사유*');
a.push('');
for (var r = 0; r < reasons.length; r++) a.push('    ' + reasons[r]);
a.push('');
a.push('━━━━━━━━━━━━━━━━━━━━━━━━━━━━');
a.push(':bar_chart:  *시스템 현황*');
a.push('');
a.push('>  상태: ' + stEmoji + ' *' + st + '*');
a.push('>  활성 스케줄러: `' + (dashboard.activeSchedulers || 0) + '`개');
a.push('>  실행중 태스크: `' + (dashboard.runningTasks || 0) + '`개');
a.push('>  최근 에러: `' + (dashboard.recentErrors || 0) + '`건');

if (taskTotal > 0 || taskStuck > 0) {
  a.push('');
  a.push(':spider_web:  *CrawlTask*  (`' + taskTotal + '`건)');
  a.push('');
  var statusEntries = Object.entries(crawlTasks.countsByStatus || {});
  if (statusEntries.length > 0) {
    for (var si = 0; si < statusEntries.length; si++) {
      var prefix = statusEntries[si][0] === 'FAILED' ? ':x: ' : '';
      a.push('>  ' + prefix + statusEntries[si][0] + ':  `' + statusEntries[si][1] + '`');
    }
  }
  if (taskStuck > 0) a.push('>  :snail: STUCK:  `' + taskStuck + '`');
}

if (tOb.total > 0 || sOb.total > 0 || pOb.total > 0) {
  a.push('');
  a.push(':inbox_tray:  *Outbox 현황*');
  a.push('>  _유형_          _대기_    _처리중_    _완료_    _실패_');
  var taskRow = fmtObRow('CrawlTask  ', tOb);
  var schedRow = fmtObRow('Scheduler    ', sOb);
  var syncRow = fmtObRow('ProductSync', pOb);
  if (taskRow) a.push(taskRow);
  if (schedRow) a.push(schedRow);
  if (syncRow) a.push(syncRow);
}

if (rawTotal > 0) {
  a.push('');
  a.push(':floppy_disk:  *CrawledRaw*  (`' + rawTotal + '`건)');
  var rawEntries = Object.entries(crawledRaw.countsByStatus || {});
  if (rawEntries.length > 0) {
    a.push('');
    for (var ri = 0; ri < rawEntries.length; ri++) {
      var rPrefix = rawEntries[ri][0] === 'FAILED' ? ':x: ' : '';
      a.push('>  ' + rPrefix + rawEntries[ri][0] + ':  `' + rawEntries[ri][1] + '`');
    }
  }
}

if (systems.length > 0) {
  a.push('');
  a.push(':satellite:  *외부 시스템*');
  a.push('');
  for (var ei = 0; ei < systems.length; ei++) {
    var ext = systems[ei];
    var badge = statusBadge(ext.status);
    var failInfo = ext.recentFailures > 0 ? '  (실패 ' + ext.recentFailures + '건)' : '';
    a.push('>  ' + ext.system + ':  ' + badge + failInfo);
  }
}

// ─── ProductSync 실패 상세 ───
if (psTotal > 0) {
  a.push('');
  a.push(':package:  *ProductSync 실패 상세*  (`' + psTotal + '`건, 대기: `' + (productSyncFailures.totalPending || 0) + '`건)');
  a.push('');
  var psSyncTypes = Object.entries(productSyncFailures.failureCountsBySyncType || {});
  for (var pi = 0; pi < psSyncTypes.length; pi++) {
    a.push('>  ' + psSyncTypes[pi][0] + ':  `' + psSyncTypes[pi][1] + '`건');
  }
  var psDetails = productSyncFailures.recentFailures || [];
  if (psDetails.length > 0) {
    a.push('>  _에러 메시지 Top 3:_');
    var topN = Math.min(psDetails.length, 3);
    for (var pd = 0; pd < topN; pd++) {
      var d = psDetails[pd];
      var msg = (d.errorMessage || '(없음)').substring(0, 80);
      a.push('>    ' + d.syncType + ' (`' + d.count + '`건): ' + msg);
    }
  }
}

// ─── CrawlExecution ───
if (execTotal > 0) {
  a.push('');
  a.push(':runner:  *CrawlExecution*  (`' + execTotal + '`건, 성공률: `' + execRate.toFixed(1) + '%`)');
  a.push('');
  var execEntries = Object.entries(crawlExecutions.countsByStatus || {});
  for (var xi = 0; xi < execEntries.length; xi++) {
    var xPrefix = (execEntries[xi][0] === 'FAILED' || execEntries[xi][0] === 'TIMEOUT') ? ':x: ' : '';
    a.push('>  ' + xPrefix + execEntries[xi][0] + ':  `' + execEntries[xi][1] + '`');
  }
}

// ─── OK Message ───
var o = [];
o.push(':large_green_circle:  *CrawlingHub 정상 가동중*');
o.push(':clock1:  ' + ts);
o.push('');
o.push('>  :bar_chart:  스케줄러 `' + (dashboard.activeSchedulers || 0) + '`개  |  태스크 `' + (dashboard.runningTasks || 0) + '`개  |  에러 `' + (dashboard.recentErrors || 0) + '`건');
if (taskTotal > 0) o.push('>  :spider_web:  CrawlTask `' + taskTotal + '`건  |  stuck `' + taskStuck + '`건');
if (rawTotal > 0) o.push('>  :floppy_disk:  CrawledRaw `' + rawTotal + '`건');
if (systems.length > 0) {
  o.push('>  :satellite:  ' + systems.map(function(s) { return s.system + ' ' + statusBadge(s.status); }).join('  |  '));
}
if (execTotal > 0) o.push('>  :runner:  CrawlExecution `' + execTotal + '`건  |  성공률 `' + execRate.toFixed(1) + '%`');

return [{
  json: {
    needsAlert: needsAlert,
    alertMessage: a.join('\n'),
    okMessage: o.join('\n')
  }
}];
