const response = $input.first().json;
const data = response.data || response;

const download = data.download || {};
const transform = data.transform || {};
const checkedAt = data.checkedAt || 'N/A';

// 임계값 설정
const PENDING_THRESHOLD = 100;
const FAILED_THRESHOLD = 0;

const hasPendingAlert = (download.pending || 0) > PENDING_THRESHOLD 
  || (transform.pending || 0) > PENDING_THRESHOLD;
const hasFailedAlert = (download.failed || 0) > FAILED_THRESHOLD 
  || (transform.failed || 0) > FAILED_THRESHOLD;

const needsAlert = hasPendingAlert || hasFailedAlert;

// 알람 사유 생성
const reasons = [];
if (download.pending > PENDING_THRESHOLD) reasons.push(`Download PENDING ${download.pending}건 (임계값: ${PENDING_THRESHOLD})`);
if (transform.pending > PENDING_THRESHOLD) reasons.push(`Transform PENDING ${transform.pending}건 (임계값: ${PENDING_THRESHOLD})`);
if (download.failed > FAILED_THRESHOLD) reasons.push(`Download FAILED ${download.failed}건`);
if (transform.failed > FAILED_THRESHOLD) reasons.push(`Transform FAILED ${transform.failed}건`);

return {
  needsAlert,
  reasons,
  download,
  transform,
  checkedAt,
  summary: {
    downloadTotal: (download.pending || 0) + (download.sent || 0) + (download.failed || 0),
    transformTotal: (transform.pending || 0) + (transform.sent || 0) + (transform.failed || 0)
  }
};

