// Static DataÏóêÏÑú ÌÜ†ÌÅ∞ Í∞ÄÏ†∏Ïò§Í∏∞
const tokenData = $workflow.staticData.token;

if (!tokenData || !tokenData.accessToken) {
  return {
    hasToken: false,
    needsRefresh: true,
    message: 'No token found'
  };
}

// ÌÜ†ÌÅ∞ ÎßåÎ£å Ï≤¥ÌÅ¨ (5Î∂Ñ Ï†ÑÏóê Í∞±Ïã†)
const expiresAt = new Date(tokenData.expiresAt);
const now = new Date();
const fiveMinutes = 5 * 60 * 1000;

if (expiresAt - now < fiveMinutes) {
  return {
    hasToken: true,
    needsRefresh: true,
    accessToken: tokenData.accessToken,
    message: 'Token expires soon'
  };
}

return {
  hasToken: true,
  needsRefresh: false,
  accessToken: tokenData.accessToken,
  message: 'Token valid'
};

const loginResponse = $input.first().json;

if (!loginResponse.success) {
  throw new Error('Login failed: ' + (loginResponse.detail || 'Unknown error'));
}

// ÌÜ†ÌÅ∞ Ï†ïÎ≥¥ Ï†ÄÏû•
const tokenData = {
  accessToken: loginResponse.data.accessToken,
  refreshToken: loginResponse.data.refreshToken,
  expiresIn: loginResponse.data.expiresIn,
  tokenType: loginResponse.data.tokenType,
  userId: loginResponse.data.userId,
  issuedAt: new Date().toISOString(),
  expiresAt: new Date(Date.now() + (loginResponse.data.expiresIn * 1000)).toISOString()
};

$workflow.staticData.token = tokenData;

return {
  accessToken: tokenData.accessToken,
  message: 'Token refreshed'
};

// Ïù¥Ï†Ñ ÎÖ∏ÎìúÏóêÏÑú ÌÜ†ÌÅ∞ Í∞ÄÏ†∏Ïò§Í∏∞ (Í∞±Ïã†Îêú ÌÜ†ÌÅ∞ ÎòêÎäî Í∏∞Ï°¥ ÌÜ†ÌÅ∞)
let accessToken;

const items = $input.all();
for (const item of items) {
  if (item.json.accessToken) {
    accessToken = item.json.accessToken;
    break;
  }
}

if (!accessToken) {
  // Static DataÏóêÏÑú Í∞ÄÏ†∏Ïò§Í∏∞
  const tokenData = $workflow.staticData.token;
  if (tokenData) {
    accessToken = tokenData.accessToken;
  }
}

if (!accessToken) {
  throw new Error('No access token available');
}

return { accessToken };

const loginResponse = $input.first().json;

if (!loginResponse.success) {
  throw new Error('Login retry failed');
}

const tokenData = {
  accessToken: loginResponse.data.accessToken,
  refreshToken: loginResponse.data.refreshToken,
  expiresIn: loginResponse.data.expiresIn,
  tokenType: loginResponse.data.tokenType,
  userId: loginResponse.data.userId,
  issuedAt: new Date().toISOString(),
  expiresAt: new Date(Date.now() + (loginResponse.data.expiresIn * 1000)).toISOString()
};

$workflow.staticData.token = tokenData;

return { accessToken: tokenData.accessToken };

// Prometheus ÌÖçÏä§Ìä∏ Ìè¨Îß∑ ÌååÏã±
const prometheusText = $input.first().json.data || $input.first().json;

function parsePrometheusMetric(text, metricName) {
  if (typeof text !== 'string') return null;
  const regex = new RegExp(`^${metricName}(?:\\{[^}]*\\})?\\s+([0-9.]+)`, 'gm');
  const matches = [...text.matchAll(regex)];
  if (matches.length === 0) return null;
  return parseFloat(matches[matches.length - 1][1]);
}

function parsePrometheusCounter(text, metricName) {
  return parsePrometheusMetric(text, metricName + '_total') || parsePrometheusMetric(text, metricName);
}

const textData = typeof prometheusText === 'string' ? prometheusText : JSON.stringify(prometheusText);

// UserAgent Pool Î©îÌä∏Î¶≠ Ï∂îÏ∂ú
const poolAvailable = parsePrometheusMetric(textData, 'useragent_pool_available') || 0;
const poolTotal = parsePrometheusMetric(textData, 'useragent_pool_total') || 0;
const poolAvailableRate = parsePrometheusMetric(textData, 'useragent_pool_available_rate') || 0;

// Counter Î©îÌä∏Î¶≠ Ï∂îÏ∂ú
const circuitBreakerTotal = parsePrometheusCounter(textData, 'useragent_circuit_breaker') || 0;
const rateLimitedTotal = parsePrometheusCounter(textData, 'useragent_rate_limited') || 0;
const suspendedTotal = parsePrometheusCounter(textData, 'useragent_suspended') || 0;
const consumeTotal = parsePrometheusCounter(textData, 'useragent_consume') || 0;
const successTotal = parsePrometheusCounter(textData, 'useragent_success') || 0;
const failureTotal = parsePrometheusCounter(textData, 'useragent_failure') || 0;

// ÏÑ±Í≥µÎ•† Í≥ÑÏÇ∞
const successRate = consumeTotal > 0 
  ? ((successTotal / consumeTotal) * 100).toFixed(2) 
  : 100;

// Alert Ï°∞Í±¥ Ï≤¥ÌÅ¨
const alerts = [];

if (poolAvailableRate < 20) {
  alerts.push({
    type: 'CRITICAL',
    metric: 'pool_available_rate',
    value: poolAvailableRate,
    message: `üö® UserAgent Pool Í∞ÄÏö©Î•† ÏúÑÌóò: ${poolAvailableRate.toFixed(1)}% (ÏûÑÍ≥ÑÍ∞í: 20%)`
  });
}

if (poolAvailable < 3) {
  alerts.push({
    type: 'CRITICAL',
    metric: 'pool_available',
    value: poolAvailable,
    message: `üö® Í∞ÄÏö© UserAgent Î∂ÄÏ°±: ${poolAvailable}Í∞ú (ÏûÑÍ≥ÑÍ∞í: 3Í∞ú)`
  });
}

if (poolAvailableRate < 50 && poolAvailableRate >= 20) {
  alerts.push({
    type: 'WARNING',
    metric: 'pool_available_rate',
    value: poolAvailableRate,
    message: `‚ö†Ô∏è UserAgent Pool Í∞ÄÏö©Î•† Ï£ºÏùò: ${poolAvailableRate.toFixed(1)}%`
  });
}

return {
  timestamp: new Date().toISOString(),
  metrics: {
    pool: {
      available: poolAvailable,
      total: poolTotal,
      availableRate: poolAvailableRate
    },
    counters: {
      circuitBreaker: circuitBreakerTotal,
      rateLimited: rateLimitedTotal,
      suspended: suspendedTotal,
      consume: consumeTotal,
      success: successTotal,
      failure: failureTotal
    },
    calculated: {
      successRate: parseFloat(successRate)
    }
  },
  alerts: alerts,
  hasAlerts: alerts.length > 0,
  hasCritical: alerts.some(a => a.type === 'CRITICAL')
};

const data = $input.first().json;
const alerts = data.alerts;
const metrics = data.metrics;

// Slack Î©îÏãúÏßÄ Íµ¨ÏÑ±
const criticalAlerts = alerts.filter(a => a.type === 'CRITICAL');
const warningAlerts = alerts.filter(a => a.type === 'WARNING');

let emoji = criticalAlerts.length > 0 ? 'üö®' : '‚ö†Ô∏è';
let color = criticalAlerts.length > 0 ? '#ff0000' : '#ffcc00';

let alertText = alerts.map(a => a.message).join('\n');

let slackMessage = {
  text: `${emoji} CrawlingHub Alert`,
  attachments: [
    {
      color: color,
      blocks: [
        {
          type: 'header',
          text: {
            type: 'plain_text',
            text: `${emoji} CrawlingHub UserAgent Pool Alert`,
            emoji: true
          }
        },
        {
          type: 'section',
          text: {
            type: 'mrkdwn',
            text: alertText
          }
        },
        {
          type: 'divider'
        },
        {
          type: 'section',
          fields: [
            {
              type: 'mrkdwn',
              text: `*Pool ÏÉÅÌÉú*\n${metrics.pool.available}/${metrics.pool.total} (${metrics.pool.availableRate.toFixed(1)}%)`
            },
            {
              type: 'mrkdwn',
              text: `*ÏÑ±Í≥µÎ•†*\n${metrics.calculated.successRate}%`
            },
            {
              type: 'mrkdwn',
              text: `*Circuit Breaker*\n${metrics.counters.circuitBreaker}Ìöå`
            },
            {
              type: 'mrkdwn',
              text: `*Rate Limited (429)*\n${metrics.counters.rateLimited}Ìöå`
            }
          ]
        },
        {
          type: 'context',
          elements: [
            {
              type: 'mrkdwn',
              text: `‚è∞ ${data.timestamp}`
            }
          ]
        }
      ]
    }
  ]
};

return { slackMessage, hasCritical: data.hasCritical };

// ÌÜ†ÌÅ∞ Í∞ÄÏ†∏Ïò§Í∏∞
const tokenData = $workflow.staticData.token;
if (!tokenData || !tokenData.accessToken) {
  throw new Error('No token available for recovery');
}
return { accessToken: tokenData.accessToken };

